import React, {Component} from 'react';
import axios from 'axios';
import './App.css';

import { getPaths } from "./constants"

class App extends Component {

    componentWillMount() {
        this.setState({
            show: false,
            result: "result"
        })
    }

    onClickExactPath = async (path) => {
        const url = getPaths("exactPath")+path
        const response = await axios.get(url);
        this.setState({
            result: response.data,
            show: true
        })
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <h2>Welcome to ThoughtWorks Train Manage System!</h2>
                </header>
                <main>
                    <div className="App-grid-container">
                        <div className="App-exact-path">
                            <input type="text" onChange={handleChange}/>
                            <button>Execute</button>
                        </div>
                        <div className="App-condition-stops">stops</div>
                        <div className="App-result">result</div>
                        <div className="App-condition-distance">distance</div>
                        <div className="App-shortest-path">{this.state.result}</div>
                    </div>
                </main>
                <footer className="App-footer">
                    All rights reserved by ThoughtWorks.
                </footer>
            </div>
        );
    }
}

export default App;
