import React, {Component} from 'react';
import axios from 'axios';
import './App.css';

import { getPaths } from "./constants"

class App extends Component {

    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
    }

    componentWillMount() {
        this.setState({
            result: "result"
        })
    }

    onClickExactPath = async () => {
        const url = getPaths("exactPath")+this.state.exactPath
        const response = await axios.get(url);
        this.setState({
            result: response.data,
        })
    }

    handleChange(event) {
        this.setState({exactPath: event.target.value});
    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <h2>Welcome to ThoughtWorks Train Manage System!</h2>
                </header>
                <main>
                    <div className="App-grid-container">
                        <div className="App-exact-path">
                            <input type="text" value={this.state.value} onChange={this.handleChange}/>
                            <button onClick={this.onClickExactPath}>Execute</button>
                        </div>
                        <div className="App-condition-stops">stops</div>
                        <div className="App-result">result</div>
                        <div className="App-condition-distance">distance</div>
                        <div className="App-shortest-path">{this.state.result}</div>
                    </div>
                </main>
                <footer className="App-footer">
                    All rights reserved by ThoughtWorks.
                </footer>
            </div>
        );
    }
}

export default App;
