import React, {Component} from 'react';
import './App.css';

class App extends Component {
    // constructor() {
    //     super();
    //     this.state.show = false
    //     this.state.items = "test"
    // }

    componentWillMount() {
        this.setState({
            show: false
        })
    }

    componentDidMount() {
        this.setState({
            show: false
        })
    }

    onClick(){
        this.setState({
            showComponentId: "te"
        })
        fetch('http://localhost:8080/shortestPath/maxDistance/C/C/30', {
            method: 'GET',
        }).then(function(response) {
            if (!response.ok) {
                throw Error(response.statusText);
            }
            return response;
        }).then(function(response) {
            this.setState({
                items: response,
                show: true
            });
        });
    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <h1>Welcome to ThoughtWorks Train Manage System!</h1>
                </header>
                <main>
                    <button onClick={ () => this.onClick() }>Test</button>
                    {this.state.show ? <p>{this.state.items}</p> : null}
                </main>
            </div>
        );
    }
}

export default App;
